components/common/footer:
{{{region name="above_footer_banner--global"}}}
{{{region name="above_footer_banner"}}}
{{#if banners.bottom}}
    {{#if settings.data_tag_enabled}}
        <div class="banners" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner banner--footer" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners" data-banner-location="bottom">
            <div class="banner banner--footer">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}
{{/if}}
{{{region name="above_footer--global"}}}
{{{region name="above_footer"}}}
<footer class="footer" role="contentinfo">
    <div class="container container--footer">
        <section class="footer-info">
            <article class="footer-info-col footer-info-col--small" data-section-type="footer-webPages">
                <h5 class="footer-info-heading">{{lang 'footer.navigate'}}</h5>
                <ul class="footer-info-list">
                    {{#each pages}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li>
                        <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                    </li>
                </ul>
            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-categories">
                <h5 class="footer-info-heading">{{lang 'footer.categories'}}</h5>
                <ul class="footer-info-list">
                    {{#each categories}}
                        {{#if ../theme_settings.hide_navigation_featured_categories}}
                            {{#if name '!==' ../../theme_settings.featured_category_name}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/if}}
                        {{else}}
                            <li>
                                <a href="{{url}}">{{name}}</a>
                            </li>
                        {{/if}}
                    {{/each}}
                </ul>
            </article>

            {{#and theme_settings.shop_by_brand_show_footer shop_by_brand.length}}
            <article class="footer-info-col footer-info-col--small" data-section-type="footer-brands">
                <h5 class="footer-info-heading">{{lang 'footer.brands'}}</h5>
                <ul class="footer-info-list">
                    {{#each shop_by_brand}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                </ul>
            </article>
            {{/and}}

            {{#if settings.address}}
            <article class="footer-info-col footer-info-col--small footer-info-col--address" data-section-type="storeInfo">
                <h5 class="footer-info-heading">{{lang 'footer.info'}}</h5>
                <div class="footer-info-list">
                    <address>{{nl2br settings.address}}</address>
                    {{#if settings.phone_number}}
                        <a  href="tel:{{settings.phone_number}}"><strong>{{lang 'footer.call_us' phone_number=settings.phone_number}}</strong></a>
                    {{/if}}
                </div>
            </article>
            {{/if}}

            <article class="footer-info-col footer-info-col--subscription" data-section-type="newsletterSubscription">
                {{#if settings.show_newsletter_box}}
                    {{> components/common/subscription-form}}
                {{/if}}
                {{#and theme_settings.social_icon_placement_bottom social_media}}
                    <div class="footer-info-col--social" data-section-type="footer-SocialIcons">
                            <h5 class="footer-info-heading">{{lang 'social.connect'}}</h5>
                            {{> components/common/social-links}}
                    </div>
                {{/and}}
                {{{region name="ssl_site_seal--global"}}}
            </article>
        </section>
    </div>
    <div class="wrapper--footerBase">
            <div class="container">
                <div class="footer-info footer-info--base">
                    <div class="footer-info-col footer-info-col--base footer-info-col--icons">
                        {{> components/f/footer/payment-icons}}
                    </div>
                    <div class="footer-info-col  footer-info-col--base">
                        {{#or theme_settings.show_designed_by theme_settings.show_powered_by}}
                            <div class="footer-copyright">
                                <p class="powered-by">
                                {{#if theme_settings.show_designed_by}}
                                    <span>{{lang 'footer.designed_by'}} <a href="https://www.flairconsultancy.com?utm_source=store&amp;utm_medium=designedbyFlair" rel="nofollow" target="_blank">Flair</a></span>
                                {{/if}}
                                {{#if theme_settings.show_powered_by}}
                                    <span>{{lang 'footer.powered_by'}} <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow" target="_blank">BigCommerce</a></span>
                                {{/if}}
                                </p>
                            </div>
                        {{/or}}
                        {{#if theme_settings.show_copyright_footer}}
                            <div class="footer-copyright">
                                <p class="powered-by">&copy; {{moment format="YYYY"}} {{settings.store_name}} </p>
                            </div>
                        {{/if}}
                        {{#if settings.shopper_consent_tracking_enabled}}
                            <div class="footer-copyright">
                                <a class="consent-manager-update" href="#" data-cookie-is-set="{{settings.privacy_cookie}}">{{lang 'footer.consent_manager'}}</a>
                            </div>
                        {{/if}}
                    </div>

                </div>
        </div>
    </div>
</footer>
{{{region name="below_footer"}}}
{{{region name="below_footer--global"}}}
{{#if theme_settings.show_newsletter_popup}}
    {{> components/f/newsletter-popup/popup}}
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'twitter'}}
                    <a class="icon icon--x" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-x" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

