pages/custom/page/page-full-width:
{{#partial "page"}}

{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

<main class="page page--full-width">

    {{{region name="above_page_title"}}}
    <h1 class="page-heading">{{ page.title }}</h1>
    {{{region name="below_page_title"}}}

    {{#if page.sub_pages}}
    {{{region name="above_page_navigation"}}}
    <nav class="navBar navBar--sub">
        <ul class="navBar-section account-navigation">
        {{#each page.sub_pages}}
            <li class="navBar-item"><a class="navBar-action" href="{{url}}">{{title}}</a></li>
        {{/each}}
        </ul>
    </nav>
    {{{region name="below_page_navigation"}}}
    {{/if}}

    <div class="page-content">
        {{{region name="above_page_content"}}}
        {{#replace '%%Syndicate%%' page.content}}
            {{> components/page/rss_feed}}
        {{else}}
            <p>{{{page.content}}}</p>
        {{/replace}}
        {{{region name="below_page_content"}}}
    </div>

    {{#if page.feed}}
        <ul>
            {{#each page.feed}}
                <li>
                    <h6><a href="{{url}}" target="_blank">{{title}}</a></h6>
                    <p>{{content}}</p>
                </li>
            {{/each}}
        </ul>
    {{/if}}
</main>

{{/partial}}

{{> layout/base}}

