        {{else}}
        <div class="productView-table-row">
            <div class="productView-table-header">{{name}}:</div>
            <div class="productView-table-data">{{{sanitize value}}}</div>
        </div>
        {{/or}}
    {{/each}}
    {{{region name="product_below_info_table"}}}
    {{{region name="product_below_info_table--global"}}}
    </div>

    {{#if product.warranty}}
        <h2 class="productView-title">{{#if theme_settings.rename_warranty_field}}{{theme_settings.rename_warranty_field}}{{else}}{{lang 'products.warranty'}}{{/if}}</h2>
        <p>{{{product.warranty}}}</p>
    {{/if}}
    {{{region name="product_below_warranty"}}}
    {{{region name="product_below_warranty--global"}}}
    </article>
{{/any}}

components/products/description:
{{#any product.description product.videos.list.length}}
<article class="productView-description" id="productDescription">
    <h2 class="productView-title">{{lang 'products.description'}}</h2>
    {{{region name="product_below_description_title"}}}
    {{{region name="product_below_description_title--global"}}}
    <div {{#if settings.data_tag_enabled}} data-event-type="product" {{/if}}>
        {{{product.description}}}
        {{{snippet 'product_description'}}}
    </div>
    {{{region name="product_below_description_body"}}}
    {{{region name="product_below_description_body--global"}}}

    {{#if product.videos.list.length}}
        <h2 class="productView-title">{{lang 'products.videos.header'}}</h2>
        {{> components/products/videos product.videos}}
    {{/if}}
{{{region name="product_below_videos"}}}
{{{region name="product_below_videos--global"}}}
</article>
{{/any}}

components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    {{#if product}}<dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>{{/if}}
    <dd class="productView-info-value">
        <a href="{{url}}"
           {{#unless is_ajax }}data-reveal-id="bulkPricingModal{{#if id}}-{{id}}-{{$index}}{{/if}}"{{/unless}}>
            {{lang 'products.bulk_pricing.view'}}
        </a>
    </dd>
    <div id="bulkPricingModal{{#if id}}-{{id}}-{{$index}}{{/if}}" class="modal modal--small" data-reveal>
        <div class="modal-header">
            <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
            <a href="#" class="modal-close" aria-label="Close"><span aria-hidden="true">&#215;</span></a>
        </div>
        <div class="modal-body">
            <p>{{lang 'products.bulk_pricing.instructions'}}</p>
            <ul>
                {{#each bulk_discount_rates}}
                <li>
                    {{lang 'products.bulk_pricing.range' min=min max=max}}
                    {{#if type '===' 'percent'}}
                    {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                    {{/if}}
                    {{#if type '===' 'fixed'}}
                    {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                    {{/if}}
                    {{#if type '===' 'price'}}
                    {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                    {{/if}}
                </li>
                {{/each}}
            </ul>
        </div>
    </div>
{{/if}}

components/products/buy-now:
{{#if theme_settings.show_buy_now}}
<div class="form-action form-action--buy-button">
    <input id="buy-now-button-main" class="button button--buy-now" type="submit" value="{{lang 'products.buy_now'}}">
</div>
{{/if}}
