pages/product:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
gql: "query productById(
    $productId: Int!
  ) {
    site {
      settings {
        tax {
          pdp
        }
      }
      product(entityId: $productId) {
        variants(isPurchasable: true, first: 250) {
          edges {
            node {
              entityId
              sku
              defaultImage {
                ...ImageFields
              }
              inventory {
                aggregated {
                  availableToSell
                  warningLevel
                }
                isInStock
              }
              productOptions {
                edges {
                  node {
                    displayName
                    ... on MultipleChoiceOption {
                      values {
                        edges {
                          node {
                            label
                          }
                        }
                      }
                    }
                  }
                }
              }
              priceWithoutTax:prices(includeTax:false) {
                price {
                  ...MoneyFields
                }
                salePrice {
                  ...MoneyFields
                }
                retailPrice {
                  ...MoneyFields
                }
                saved {
                  ...MoneyFields
                }
                bulkPricing {
                  minimumQuantity
                  maximumQuantity
                  ... on BulkPricingFixedPriceDiscount {
                    price
                  }
                  ... on BulkPricingPercentageDiscount {
                    percentOff
                  }
                  ... on BulkPricingRelativePriceDiscount {
                    priceAdjustment
                  }
                }
              }
              priceWithTax:prices(includeTax:true) {
                price {
                  ...MoneyFields
                }
                salePrice {
                  ...MoneyFields
                }
                retailPrice {
                  ...MoneyFields
                }
                saved {
                  ...MoneyFields
                }
                bulkPricing {
                  minimumQuantity
                  maximumQuantity
                  ... on BulkPricingFixedPriceDiscount {
                    price
                  }
                  ... on BulkPricingPercentageDiscount {
                    percentOff
                  }
                  ... on BulkPricingRelativePriceDiscount {
                    priceAdjustment
                  }
                }
              }
            }
          }
        }
      }
    }
  }

  fragment ImageFields on Image {
    imageUrl: url(width: 120)
  }

  fragment MoneyFields on Money {
    formatted
  }"
---
{{#unless theme_settings.performance_mode_swatches}}
    {{inject "enableSwatches" theme_settings.show_swatches_on_grid}}
{{/unless}}
{{inject "enableImageHoverSwitcher" theme_settings.enable_image_hover_switcher}}
{{inject "enableSelectedOptionLabel" theme_settings.show_selected_option_on_label}}
{{inject "addToCartMode" theme_settings.add_to_cart_mode}}
{{inject "watchers" theme_settings.show_watchers}}
{{inject "watchers_min" (toInt theme_settings.watchers_min)}}
{{inject "watchers_max" (toInt theme_settings.watchers_max)}}
{{inject "watchers_update" (toInt theme_settings.watchers_update)}}
{{#partial "page"}}

    <div>
        <div class="product-breadcrumbs">
            {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
        </div>

        {{#each product.shipping_messages}}
            <div class="container upsell">
                {{#if type '===' 2}}
                    {{> components/common/alert-success message}}
                {{else}}
                    {{> components/common/alert-info message}}
                {{/if}}
            </div>
        {{/each}}

        {{#each product.reviews.messages}}
            <div class="container upsell">
                {{#if error}}
                    {{> components/common/alert-error error}}
                {{/if}}
                {{#if success}}
                    {{> components/common/alert-success success}}
                {{/if}}
            </div>
        {{/each}}

        {{> components/products/product-view settings=../settings}}

        <div class="container">
            {{{region name="product_above_reviews"}}}
            {{{region name="product_above_reviews--global"}}}
            {{#and settings.show_product_reviews product.reviews.total}}
                {{#if product.num_reviews "!==" 0}}
                    {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
                {{/if}}
            {{/and}}
            {{#if theme_settings.enable_editorial_mode}}
                {{> components/f/products/related-article}}
            {{/if}}
            {{{region name="product_below_reviews"}}}
            {{{region name="product_below_reviews--global"}}}

            {{#or product.related_products product.similar_by_views}}
            {{{region name="product_above_cross_sell"}}}
            {{{region name="product_above_cross_sell--global"}}}
            <div class="productView-carousel">
              {{#if product.related_products}}
              {{{region name="product_above_related_products"}}}
              {{{region name="product_above_related_products--global"}}}
              <h2 class="productView-title">{{lang 'products.related_products'}}</h2>
              {{#if settings.data_tag_enabled}}
                  {{> components/products/carousel settings=../settings products=product.related_products columns=5 list="Related Products" panel="Related Products"}}
              {{else}}
                  {{> components/products/carousel products=product.related_products columns=5 list="Related Products" panel="Related Products"}}
              {{/if}}
              {{{region name="product_below_related_products"}}}
              {{{region name="product_below_related_products--global"}}}
              {{/if}}


              {{#and theme_settings.show_customer_recent_items customer.recently_viewed_products.items}}
                  <h2 class="productView-title">{{lang 'account.recent_items.heading' }}</h2>
                  {{#if settings.data_tag_enabled}}
                      {{> components/products/carousel products=customer.recently_viewed_products.items theme_settings=theme_settings columns=5 urls=urls event="list" panel="RecentProducts"}}
                  {{else}}
                      {{> components/products/carousel products=customer.recently_viewed_products.items theme_settings=theme_settings columns=5 urls=urls panel="RecentProducts"}}
                  {{/if}}
              {{/and}}


              {{#if product.similar_by_views}}
              {{{region name="product_above_similar_by_views"}}}
              {{{region name="product_above_similar_by_views--global"}}}
              <h2 class="productView-title">{{lang 'products.similar_by_views'}}</h2>
              {{> components/products/carousel settings=../settings products=product.similar_by_views columns=5 list="Customers Also Viewed" panel="CustomersViewed"}}
              {{{region name="product_below_similar_by_views"}}}
              {{{region name="product_below_similar_by_views--global"}}}
              {{/if}}
            </div>
            {{{region name="product_below_cross_sell"}}}
            {{{region name="product_below_cross_sell--global"}}}
            {{/or}}
        </div>
    </div>
    {{> components/products/schema}}
{{/partial}}
{{> layout/base}}

components/products/reviews:
{{#if reviews.total}}
<ul class="productReviews-list" id="productReviews-list">
  <h2 class="productView-title productView-title--reviews" id="product-reviews">
    {{lang 'products.reviews.title' reviews=product.num_reviews}}
  </h2>
  {{#each reviews.list}}
  <li class="productReview">
    <article>
      <header>
        {{#if name}}
        <p class="productReview-author">
          {{{lang 'products.reviews.post_on_by' date=date name=(sanitize name)
          }}}
        </p>
        {{else}}
        <p class="productReview-author">
          {{lang 'products.reviews.post_on_by' date=date name=(lang
          'products.reviews.anonymous_poster')}}
        </p>
        {{/if}}
        <span class="productReview-rating rating--small">
          {{> components/products/ratings rating=rating}}
          <span class="productReview-ratingNumber">{{ rating }}</span
          >
        </span>
        <h4 class="productReview-title">
          {{{sanitize title }}}
        </h4>
      </header>
      <p class="productReview-body">
        {{{sanitize text}}}
      </p>
    </article>
  </li>
  {{/each}} {{> components/common/paginator pagination.reviews}}
</ul>
{{/if}} {{{snippet 'reviews'}}}

components/products/product-view:
{{inject "productOptionsGql" gql.data.site.product.variants.edges}}
{{inject "productTaxPriceSettings" gql.data.site.settings.tax.pdp}}
{{inject "productTaxLabel" product.price.tax_label}}
{{#filter product.custom_fields "Theme_OGP" property="name"}}
    {{inject "productGridColumnLabels" this.value}}
{{/filter}}
{{inject "productId" product.id}}
<div class="productView" {{#if settings.data_tag_enabled}} data-event-type="product" data-entity-id="{{product.id}}" data-name="{{product.title}}" data-product-category="{{#each product.category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}" data-product-brand="{{product.brand.name}}" data-product-price="{{#if product.price.with_tax}}{{product.price.with_tax.value}}{{else}}{{product.price.without_tax.value}}{{/if}}" data-product-variant="single-product-option"{{/if}}>
    <div class="productView-inner container">
        <section class="productView-details productView-details--header">
            <div class="productView-product">
                {{{region name="product_above_title"}}}
                {{{region name="product_above_title--global"}}}
                <h1 class="productView-title">{{product.title}}</h1>
                {{{region name="product_below_title"}}}
                {{{region name="product_below_title--global"}}}
                {{#if product.brand}}
                <h2 class="productView-brand">
                    <a href="{{product.brand.url}}"><span>{{product.brand.name}}</span></a>
                </h2>
                {{{region name="product_below_brand"}}}
                {{{region name="product_below_brand--global"}}}
                {{/if}}
                <div class="productView-rating">
                    {{#if settings.show_product_rating}}
                        {{#if product.num_reviews '>' 0}}
                            {{> components/products/ratings rating=product.rating}}
                        {{/if}}
                        <span class="productView-reviewLink">
                            {{#if product.num_reviews '>' 0}}
                                <a href="{{product.url}}#product-reviews">
                                    {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                                </a>
                            {{else}}
                                {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                            {{/if}}
                        </span>
                    {{/if}}
                    {{#if settings.show_product_reviews}}
                        <span class="productView-reviewLink">
                            <a href="{{product.url}}{{#if is_ajax}}#write_review{{/if}}"
                               {{#unless is_ajax }}data-reveal-id="modal-review-form"{{/unless}}>
                               {{lang 'products.reviews.new'}}
                            </a>
                        </span>
                        {{> components/products/modals/writeReview}}
                    {{/if}}
                </div>
                <dl>
                    <dt class="productView-info-name productView-info-name--sku"{{#unless product.sku}} style="display: none;"{{/unless}}>{{lang 'products.sku'}}</dt>
                    <dd class="productView-info-value productView-info-value--sku" data-product-sku>{{product.sku}}</dd>
                </dl>
            </div>
            {{#if product.call_for_price}}
            <p class="productView-price">
                <span>{{product.call_for_price}}</span>
            </p>
        {{/if}}
        {{#or (if theme_settings.show_price_below_name '===' 'middle') (if theme_settings.show_price_below_name '===' 'both')}}
        <div class="productView-price">
            {{#or theme_settings.show_sale_tags theme_settings.show_custom_product_labels theme_settings.show_selling_fast_tags}}
            <div class="card-tags">
                {{#or product.price.sale_price_with_tax product.price.sale_price_without_tax}}
                    {{#if theme_settings.show_sale_tags}}
                        {{> components/f/tags/sale-tag price=product.price}}
                    {{/if}}
                {{/or}}
                {{#if theme_settings.show_custom_product_labels}}
                    {{#each product.custom_fields}}
                        {{#or (if this.name '===' 'Promotion Label') (if name '===' 'GridTag')}}
                            {{> components/f/tags/custom-tag}}
                        {{/or}}
                    {{/each}}
                {{/if}}
            </div>
        {{/or}}
            {{#or customer (if theme_settings.restrict_to_login '===' 0)}}
                {{#and theme_settings.restrict_new_accounts (if customer_group_id '===' 0)}}
                    {{> components/common/login-for-pricing deactivated=true}}
                {{else}}
                    {{> components/f/products/price price=product.price}}
                {{/and}}
            {{else}}
                {{> components/common/login-for-pricing}}
            {{/or}}
        </div>
        {{/or}}
        {{{region name="product_below_price"}}}
        {{{region name="product_below_price--global"}}}
        </section>

        <section class="productView-images" data-image-gallery data-sticky-container="true">
          {{!--
            Note that these image sizes are coupled to image sizes used in /assets/js/theme/common/product-details.js
            for variant/rule image replacement
        --}}
            <div class="sticky-product" data-sticky>
                <figure class="productView-image"
                        data-image-gallery-main
                        {{#if product.main_image}}
                        data-zoom-image="{{getImageSrcset product.main_image (cdn theme_settings.default_image_product) 1x=theme_settings.zoom_size }}"
                        {{/if}}
                        >
                    <div class="productView-img-container">
                        {{!-- Remove the surrounding a-element if there is no main image. --}}
                        {{#if product.main_image}}
                        <a href="{{getImageSrcset product.main_image (cdn theme_settings.default_image_product) 1x=theme_settings.zoom_size}}"
                    target="_blank">
                        {{/if}}

                        {{> components/common/responsive-img
                            image=product.main_image
                            class="productView-image--default"
                            fallback_size=theme_settings.product_size
                            lazyload=theme_settings.lazyload_mode
                            default_image=theme_settings.default_image_product
                            otherAttributes="data-main-image"
                        }}

                        {{#if product.main_image}}
                            </a>
                        {{/if}}
                    </div>
                </figure>
                {{#gt product.images.length 1}}
                <ul class="productView-thumbnails"{{#gt product.images.length 5}} data-slick='{
                        "infinite": false,
                        "mobileFirst": true,
                        "slidesToShow": 3,
                        "slidesToScroll": 1,
                        "dots": false,
                        "useTransform": true,
                        "swipe": true,
                        "draggable": true,
                        "responsive": [
                            {
                                "breakpoint": 550,
                                "settings": {
                                  "slidesToShow": 4
                                }
                            },
                            {
                                "breakpoint": 800,
                                "settings": {
                                  "slidesToShow": 5
                                }
                            },
                            {
                                "breakpoint": 1260,
                                "settings": {
                                  "slidesToShow": 5,
                                  "vertical": true,
                                  "verticalSwiping": true,
                                  "adaptiveHeight": true
                                }
                            }
                        ]
                    }'{{/gt}}>
                    {{#each product.images}}
                        <li class="productView-thumbnail">
                            <a
                                class="productView-thumbnail-link"
                                href="{{getImage this 'product_size' (cdn ../theme_settings.default_image_product)}}"
                                data-image-gallery-item
                                data-image-gallery-new-image-url="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.product_size}}"
                        data-image-gallery-new-image-srcset="{{getImageSrcset this use_default_sizes=true}}"
                        data-image-gallery-zoom-image-url="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}">
                                {{> components/common/responsive-img
                                    image=this
                                    fallback_size=../theme_settings.productview_thumb_size
                                    lazyload=../theme_settings.lazyload_mode
                                }}
                            </a>
                        </li>
                    {{/each}}
                </ul>
                {{/gt}}
            </div>
        </section>

        {{product.detail_messages}}
        <dl class="productView-info">
            {{#if theme_settings.show-upc-on-product}}
                <dt class="productView-info-name upc-label"{{#unless product.upc}} style="display: none;"{{/unless}}>{{lang 'products.upc'}}</dt>
                <dd class="productView-info-value" data-product-upc>{{product.upc}}</dd>
            {{/if}}
            {{#if product.condition}}
                <dt class="productView-info-name">{{lang 'products.condition'}}</dt>
                <dd class="productView-info-value">{{product.condition}}</dd>
            {{/if}}
            {{#all product.width product.height product.depth theme_settings.show_product_dimensions}}
                <dt class="productView-info-name">{{lang 'products.width'}}</dt>
                <dd class="productView-info-value" data-product-width>
                    {{product.width}}
                    {{#if settings.measurements.length '==' 'Centimeters'}}
                    ({{lang 'products.measurement.metric'}})
                    {{else}}
                    ({{lang 'products.measurement.imperial'}})
                    {{/if}}
                </dd>
                <dt class="productView-info-name">{{lang 'products.height'}}</dt>
                <dd class="productView-info-value" data-product-height>
                    {{product.height}}
                    {{#if settings.measurements.length '==' 'Centimeters'}}
                    ({{lang 'products.measurement.metric'}})
                    {{else}}
                    ({{lang 'products.measurement.imperial'}})
                    {{/if}}
                </dd>
                <dt class="productView-info-name">{{lang 'products.depth'}}</dt>
                <dd class="productView-info-value" data-product-depth>
                    {{product.depth}}
                    {{#if settings.measurements.length '==' 'Centimeters'}}
                    ({{lang 'products.measurement.metric'}})
                    {{else}}
                    ({{lang 'products.measurement.imperial'}})
                    {{/if}}
                </dd>
            {{/all}}
            {{#if product.min_purchase_quantity}}
                <dt class="productView-info-name">{{lang 'products.min_purchase_quantity'}}</dt>
                <dd class="productView-info-value">{{lang 'products.purchase_units' quantity=product.min_purchase_quantity}}</dd>
            {{/if}}
            {{#if product.max_purchase_quantity}}
                <dt class="productView-info-name">{{lang 'products.max_purchase_quantity'}}</dt>
                <dd class="productView-info-value">{{lang 'products.purchase_units' quantity=product.max_purchase_quantity}}</dd>
            {{/if}}
            {{#if product.gift_wrapping_available}}
                <dt class="productView-info-name">{{lang 'products.gift_wrapping'}}</dt>
                <dd class="productView-info-value">{{lang 'products.gift_wrapping_available'}}</dd>
            {{/if}}
            {{#if product.shipping}}
                {{#if product.shipping.calculated}}
                    <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                    <dd class="productView-info-value">{{lang 'products.shipping_calculated'}}</dd>
                {{else}}
                    {{#if product.shipping.price.value '===' 0}}
                        <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                        <dd class="productView-info-value">{{lang 'products.shipping_free'}}</dd>
                    {{else}}
                        <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                        <dd class="productView-info-value">{{lang 'products.shipping_fixed' amount=product.shipping.price.formatted}}</dd>
                    {{/if}}
                {{/if}}
            {{/if}}

            {{#each product.custom_fields url=product.url}}
                {{#if name '===' 'Short Description'}}
                    <div class="productView-info-value productView-info-value--short-description">
                        {{{value}}}
                        <a href="{{#if ../../is_ajax}}{{../../../product.url}}{{/if}}#productDescription">{{lang 'products.view_full_description'}}</a>
                    </div>
                {{/if}}
            {{/each}}

            {{{region name="product_below_short_description"}}}
            {{{region name="product_below_short_description--global"}}}
        </dl>

        <section class="productView-details buy-section">
            {{#if product.call_for_price}}
                <p class="productView-price">
                    <span>{{product.call_for_price}}</span>
                </p>
            {{/if}}

            {{#unless theme_settings.show_price_below_name '===' 'middle'}}
            <div class="productView-price">
                {{#or theme_settings.show_sale_tags theme_settings.show_custom_product_labels theme_settings.show_selling_fast_tags}}
                {{#if theme_settings.show_price_below_name '===' 'right'}}
                <div class="card-tags">
                    {{#or product.price.sale_price_with_tax product.price.sale_price_without_tax}}
                        {{#if theme_settings.show_sale_tags}}
                            {{> components/f/tags/sale-tag price=product.price}}
                        {{/if}}
                    {{/or}}
                </div>
                {{/if}}
                {{/or}}
                {{#or customer (if theme_settings.restrict_to_login '===' 0)}}
                    {{#and theme_settings.restrict_new_accounts (if customer_group_id '===' 0)}}
                        {{> components/common/login-for-pricing deactivated=true}}
                    {{else}}
                        {{> components/f/products/price price=product.price}}
                    {{/and}}
                {{else}}
                    {{> components/common/login-for-pricing}}
                {{/or}}
            </div>
            {{/unless}}
            <div class="productView-options">
                {{#if product.release_date }}
                    <p>{{product.release_date}}</p>
                {{/if}}
                <form class="form 
                {{#if theme_settings.enable_circular_swatches}}swatches--round{{/if}} 
                {{#if theme_settings.show_buy_now}}has-buy-now{{/if}}" 
                method="post" 
                action="{{product.cart_url}}" 
                enctype="multipart/form-data"
                        data-cart-item-add>
                    <input type="hidden" name="action" value="add" id="atc-form-action">
                    <input type="hidden" name="product_id" value="{{product.id}}"/>
                    <div data-product-option-change style="display:none;">
                        {{#each product.options}}
                            {{{dynamicComponent 'components/products/options'}}}
                        {{/each}}
                    </div>

                    {{> components/products/add-to-cart with_wallet_buttons=true}}
                    {{#if product.out_of_stock}}
                        {{> components/f/products/stock}}
                    {{/if}}
                </form>
                {{#if settings.show_wishlist}}
                    {{> components/common/wishlist-dropdown}}
                {{/if}}
            </div>
            {{>components/common/share url=product.url}}
            {{{snippet 'product_details'}}}
        </section>
    </div>
</div>
<div class="container productView-description stack has-options-grid">
    <div id="optionsGrid"></div>

</div>

{{{region name="product_above_description"}}}
{{{region name="product_above_description--global"}}}
    <div class="container product-description {{#if theme_settings.stack_product_description}}stack{{/if}}">
        {{> components/products/description}}
        {{> components/products/extra-information}}
    </div>
{{{region name="product_below_description"}}}
{{{region name="product_below_description--global"}}}

<div id="previewModal" class="modal modal--large" data-reveal>
    <a href="#" class="modal-close" aria-label="{{lang 'common.close'}}" role="button">
        <span aria-hidden="true">&#215;</span>
    </a>
    <div class="modal-content"></div>
    <div class="loadingOverlay"></div>
</div>

components/f/products/related-article:
{{#each product.custom_fields}}
    {{#if name '===' 'Related Content'}}
        {{inject "relatedContent" value}}
        <div class="relatedContent">
            <h2 class="page-heading">{{lang 'products.related_content'}}</h2>
        </div>
    {{/if}}
{{/each}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

components/f/products/card:
{{inject 'itemAdded' (lang 'products.cards.item_added')}}

<article class="card {{#if theme_settings.enable_side_cart}}card--cart-enabled{{else}}card--cart-disabled{{/if}} {{#if alternate}}card--alternate{{/if}} {{#unless price}}no-price{{/unless}}" {{#if settings.data_tag_enabled}} data-event-type="{{event}}" data-entity-id="{{id}}" data-position="{{position}}" data-name="{{name}}" data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}" data-product-brand="{{brand.name}}" data-product-price="{{#if price.with_tax}}{{price.with_tax.value}}{{else}}{{price.without_tax.value}}{{/if}}"  {{/if}}>

    <div class="card-section-lefttWrapper">
        <figure class="card-figure">
        {{#if demo}}
            <div class="card-img-container"> 
                {{> components/common/responsive-img
                    image=image
                    class="card-image"
                    fallback_size=theme_settings.productgallery_size
                    lazyload=theme_settings.lazyload_mode
                    default_image=theme_settings.default_image_product
                }}
            </div>
        {{else}}
            <a class="card-figure-link" href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>
                {{#or theme_settings.show_sale_tags theme_settings.show_custom_product_labels theme_settings.show_selling_fast_tags}}
                <div class="card-tags">
                    {{#or price.sale_price_with_tax price.sale_price_without_tax}}
                        {{#if theme_settings.show_sale_tags}}
                            {{> components/f/tags/sale-tag price=price}}
                        {{/if}}
                    {{/or}}
                    {{#if theme_settings.show_custom_product_labels}}
                        {{#each custom_fields}}
                            {{#or (if this.name '===' 'Promotion Label') (if name '===' 'GridTag')}}
                                {{> components/f/tags/custom-tag}}
                            {{/or}}
                        {{/each}}
                    {{/if}}
                    {{#if theme_settings.show_selling_fast_tags}}
                        {{#if stock_level}}
                            {{#if stock_level '<=' low_stock_level}}
                                {{> components/f/tags/selling-fast}}
                            {{/if}}
                        {{/if}}
                    {{/if}}
                </div>
            {{/or}}
                <div class="card-img-container">
                    {{> components/common/responsive-img
                        image=image
                        class="card-image"
                        fallback_size=theme_settings.productgallery_size
                        lazyload=theme_settings.lazyload_mode
                        default_image=theme_settings.default_image_product
                    }}
                </div>
            </a>
        {{/if}}
        <figcaption class="card-figcaption">
            <div class="card-figcaption-body">
                {{#unless hide_product_quick_view}}
                    {{#if theme_settings.show_product_quick_view}}
                        {{#unless demo}}
                            {{#if settings.data_tag_enabled}}
                                <a href="#" class="button button--small card-figcaption-button quickview" data-event-type="product-click" data-product-id="{{id}}" data-tax-label="{{price.tax_label}}" data-grid-sort="{{#filter custom_fields 'Theme_OGP' property='name'}}{{~inject 'productGridColumnLabels' this.value~}}{{/filter}}">{{lang 'products.quick_view'}}</a>
                            {{else}}
                                <a href="#" class="button button--small card-figcaption-button quickview" data-product-id="{{id}}" data-tax-label="{{price.tax_label}}" data-grid-sort="{{#filter custom_fields 'Theme_OGP' property='name'}}{{~this.value~}}{{/filter}}">{{lang 'products.quick_view'}}</a>
                            {{/if}}
                        {{/unless}}
                    {{/if}}
                {{/unless}}
            </div>
        </figcaption>
    </figure>
    {{#if show_compare}}
    <label class="button button--compare" for="compare-{{id}}">
        <input type="checkbox" name="products[]" value="{{id}}" id="compare-{{id}}" data-compare-id="{{id}}"> {{lang 'products.compare'}}
    </label>
    {{/if}}
    </div>
    <div class="card-section card-section--body">
        {{#or brand.name sku}}
            <div class="card-brandwrap">
                {{#if brand.name}}
                    <p class="card-text card-text--brand" data-test-info-type="brandName">{{brand.name}}</p>
                {{/if}}
                {{#if sku}}
                    <p class="card-text card-text--sku">{{lang 'products.sku'}} {{sku}}</p>
                {{/if}}
            </div>
        {{/or}}
        <h4 class="card-title">
            {{#if demo}}
                {{name}}
            {{else}}
                <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>{{name}}</a>
            {{/if}}
        </h4>
        {{#if show_rating}}
            {{#if num_reviews}}
                <p class="card-text" data-test-info-type="productRating">
                    <span class="rating--small">
                        {{> components/products/ratings rating=rating}}
                    </span>

                    <span class="cardRating-count"><a href="{{url}}#product-reviews">{{lang 'products.reviews.link_to_review' total=num_reviews}}</a></span>
                </p>
            {{/if}}
        {{/if}}
        {{#if theme_settings.show_sizes_on_grid}}
            {{#if has_options}}
            <div class="card-sizes" data-url="{{url}}" data-id="{{id}}">
            </div>
            {{/if}}
        {{/if}}

        <div class="custom-fields">
        {{#if theme_settings.show_custom_fields_on_grid}}
            {{#each custom_fields}}
                {{#or (if name '===' 'Blog Post Description') (if name '===' 'Promotion Label') (if name '===' 'Related Content') (if name '===' 'GridTag') (if name '===' 'Short Description') (if name '===' 'Theme_OGP')}}
                {{else}}
                <div>
                    <span class="card-label-name">{{name}}:</span>
                    <span class="card-label-value">{{{value}}}</span>
                </div>
                {{/or}}
            {{/each}}
        {{/if}}
        </div>
</div>
<div class="card-section-rightWrapper">
    <div class="card-section card-section--price">
        <div class="card-text card-text--price" data-test-info-type="price">
            {{#or customer (if theme_settings.restrict_to_login '===' 0)}}
                {{#and theme_settings.restrict_new_accounts (if customer_group_id '===' 0)}}
                    {{> components/common/login-for-pricing deactivated=true}}
                {{else}}
                    {{> components/f/products/price price=price}}
                {{/and}}
            {{else}}
                {{> components/common/login-for-pricing}}
            {{/or}}
        </div>
        {{> components/products/bulk-discount-rates}}
    </div>
        <div class="card-section-rightWrapper--inner">
            {{#or customer (if theme_settings.restrict_to_login '===' 0)}}
                {{#and theme_settings.restrict_new_accounts (if customer_group_id '===' 0)}}
                {{else}}
                    {{#or theme_settings.show_availability_on_grid theme_settings.show_stock_level_on_grid}}
                        <div class="card-section card-section--availability{{#if availability}} card-section--show{{/if}}{{#and (if stock_level '!==' null) theme_settings.show_stock_level_on_grid}} card-section--show{{/and}}">
                            {{#and theme_settings.show_availability_on_grid availability}}
                                <div class="card-availability">{{availability}}</div>
                            {{/and}}


                            {{#and stock_level theme_settings.show_stock_level_on_grid}}
                                {{#if stock_level '===' 0}}
                                    <div class="card-stock card-stock--outStock">{{lang 'products.cards.out_of_stock'}}</div>
                                {{else}}
                                    {{#if stock_level '<=' low_stock_level}}
                                        <div class="card-stock card-stock--lowStock">{{lang 'products.cards.low_stock' quantity=stock_level}}</div>
                                        {{else}}
                                        <div class="card-stock card-stock--inStock">{{lang 'products.cards.in_stock' quantity=stock_level}}</div>
                                    {{/if}}
                                {{/if}}
                            {{/and}}
                            {{#if theme_settings.display_product_in_cart}}
                                <div class="card-qtyAdded" data-qtyInCart="{{id}}">{{#if qty_in_cart}}{{lang 'b2b.already_in_cart' number=qty_in_cart}}{{/if}}</div>
                            {{/if}}
                        </div>
                    {{/or}}

                    <div class="card-section--buttons {{#if add_to_cart_url}}card-section--quantity{{/if}}">
                        {{#if show_cart_action}}
                            {{#if add_to_cart_url }}
                                {{#if theme_settings.show_card_qty_input}}
                                    <form class="form form-cardAdd" method="post" action="{{urls.cart}}" enctype="multipart/form-data" data-cart-item-add-from-card>
                                        <div class="add-card-popup"></div>
                                        <input type="hidden" name="action" value="add">
                                        <input type="hidden" name="product_id" value="{{id}}"/>
                                        <input class="form-input form-input-qty"
                                               name="qty[]"
                                               type="tel"
                                               value="{{#if min_purchase_quantity}}{{min_purchase_quantity}}{{else}}1{{/if}}"
                                               data-quantity-min="{{min_purchase_quantity}}"
                                               data-quantity-max="{{max_purchase_quantity}}"
                                               min="{{#if min_purchase_quantity}}{{min_purchase_quantity}}{{else}}1{{/if}}"
                                               {{#if max_purchase_quantity}}max="{{max_purchase_quantity}}"{{/if}}
                                               pattern="[0-9]*"
                                               aria-live="polite"
                                               autocomplete="off">
                                        <input data-wait-message="{{lang 'products.adding_to_cart'}}" data-event-type="product-click" data-product-id="{{id}}" class="button button--primary button--cardAdd" type="submit"
                                               value="{{#if pre_order}}{{lang 'products.pre_order'}}{{else}}{{lang 'products.add_to_cart'}}{{/if}}">
                                    </form>
                                {{else}}
                                    <form class="form form-cardAdd" method="post" action="{{urls.cart}}" enctype="multipart/form-data" data-cart-item-add-from-card>
                                        <div class="add-card-popup"></div>
                                        <input type="hidden" name="action" value="add">
                                        <input type="hidden" name="product_id" value="{{id}}"/>
                                        <input data-wait-message="{{lang 'products.adding_to_cart'}}" data-event-type="product-click" data-product-id="{{id}}" class="button button--primary button--cardAdd" type="submit"
                                               value="{{#if pre_order}}{{lang 'products.pre_order'}}{{else}}{{lang 'products.add_to_cart'}}{{/if}}">
                                    </form>
                                {{/if}}
                            {{/if}}
                            {{#if has_options}}
                                <a href="{{url}}" data-event-type="product-click" class="button button--primary button--cardAction{{#if theme_settings.choose-options-opens-quickview}} quickview{{/if}}" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                            {{/if}}
                            {{#if pre_order}}
                                <a href="{{pre_order_add_to_cart_url}}" data-event-type="product-click" class="button button--primary button--cardAction">{{lang 'products.pre_order'}}</a>
                            {{/if}}
                            {{#if out_of_stock_message }}
                                <a href="{{url}}" data-event-type="product-click" class="button button--cardAction button--out-of-stock" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                            {{/if}}
                        {{/if}}
                        {{#unless show_remove}}
                            {{#and theme_settings.show_wishlist_on_grid settings.show_wishlist}}
                                <form action="{{add_to_wishlist_url}}" method="post">
                                    <a {{#unless customer.wishlists}}href="/wishlist.php?action=addwishlist&product_id={{id}}"{{/unless}} class="button button-wishlist" aria-label="{{lang 'account.wishlists.add_item'}}" title="{{lang 'account.wishlists.add_item'}}"{{#if customer.wishlists}} data-dropdown="wishlistsCardMenu{{capitalize panel}}-{{id}}" aria-controls="wishlistsCardMenu{{capitalize panel}}-{{id}}" aria-expanded="false" data-options="align: top"{{/if}}><span class="button-wishlist--text">{{lang 'account.wishlists.add_item'}}</span><div class="button-wishlist-star">
                                            <svg>
                                                <use xlink:href="#icon-star-outline"></use>
                                            </svg>
                                        </div>
                                    </a>
                                    {{#if customer.wishlists}}
                                        <ul class="dropdown-menu dropdown-menu--arrow-right" id="wishlistsCardMenu{{capitalize panel}}-{{id}}" data-dropdown-content aria-hidden="true" tabindex="-1">
                                        {{#each customer.wishlists}}
                                            <li class="dropdown-menu-item">
                                                <input class="card-add-to-wishlist" formaction="{{add_url}}&product_id={{../id}}" type="submit" value="{{name}}">
                                            </li>
                                        {{/each}}
                                            <li class="dropdown-menu-item">
                                                <a href="/wishlist.php?action=addwishlist&product_id={{id}}">{{lang 'account.wishlists.create_new'}}</a>
                                            </li>
                                        </ul>
                                    {{/if}}
                                </form>
                            {{/and}}
                        {{else}}
                            <form action="{{remove_url}}" class="form form-remove" method="post">
                                <input type="submit" class="button" value="{{lang 'account.wishlists.remove_item'}}" />
                            </form>
                        {{/unless}}
                    </div>
                {{/and}}
            {{/or}}
        </div>
    </div>
</article>

