<script>
    function hideNotificationStripInPreviewMode() {
        const observer = new MutationObserver((mutationsList) => {
            mutationsList.forEach((mutation) => {
                const previewModeStyle = document.querySelector('head style#BC-SD-hide-overlay');
                const notificationStrip = document.getElementById('theme-documentation-strip');

                if (previewModeStyle && notificationStrip) {
                    notificationStrip.style.display = 'none';
                } else if (!previewModeStyle && notificationStrip) {
                    notificationStrip.style.display = 'flex';
                }
            });
        });

        const head = document.querySelector('head');
        observer.observe(head, { childList: true });
  }

  function prependStyledDivWithLinksToParentOnce() {
    var pb = document;

    var themeNotification = 'theme-documentation-strip';

    if (!pb.getElementById(themeNotification)) {
      var pbWrapper = pb.body;

      var createNotification = pb.createElement('div');
      createNotification.id = themeNotification;

      createNotification.style.backgroundColor = 'rgb(49, 52, 64)';
      createNotification.style.borderBottom = '1px solid rgb(217, 220, 233)';
      createNotification.style.padding = '14px';
      createNotification.style.color = '#ffffff';
      createNotification.style.fontSize = '14px';
      createNotification.style.textAlign = 'center';
      createNotification.style.display = 'flex';
      createNotification.style.justifyContent = 'space-between';
      createNotification.style.zIndex = '999999';

      createNotification.innerHTML = `
        <div>
          {{theme_settings.n}} {{> components/common/version}} by Flair. 
          <span style="margin-left: 5px;">Contact support@flairconsultancy.com for help.</span> 
        </div>
        <div>
          <a href="https://www.flairthemes.com/category/4-camden-theme" target="_blank" style="color: #ffffff; margin-left: 16px;">Documentation</a> 
          <a href="https://www.flairconsultancy.com/support" target="_blank" style="color: #ffffff; margin-left: 16px;">Register your theme</a> 
          <a href="https://www.flairconsultancy.com/services/theme-onboarding/" target="_blank" style="color: #ffffff; margin-left: 16px;">Theme onboarding service</a> 
          <a href="https://www.flairconsultancy.com/theme-customization/" target="_blank" style="color: #ffffff; margin-left: 16px;">Get a customization quote</a> 
        </div>
      `;

      if (pbWrapper.firstChild) {
        pbWrapper.insertBefore(createNotification, pbWrapper.firstChild);
      } else {
        pbWrapper.appendChild(createNotification);
      }

      var themeDocumentationStrip = pb.getElementById('theme-documentation-strip');

      var links = createNotification.querySelectorAll('a');
      links.forEach(function(link) {
        link.addEventListener('click', function(event) {
          event.preventDefault();
          var targetUrl = link.href;
          window.open(targetUrl, '_blank');
        });
      });
    }

    hideNotificationStripInPreviewMode();
  }

  if (window.self !== window.top) {
    prependStyledDivWithLinksToParentOnce();
  }
</script>
<style>
    #theme-documentation-strip a {
        pointer-events: all !important;
    }
</style>
{{/contains}}

components/f/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}
{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/f/products/price-range price=price}}
{{else}}
        <div class="{{#unless theme_settings.show_price_below_name '===' 'right'}}buy-section-price{{/unless}} price-section"{{#or price.rrp_with_tax price.rrp_without_tax}}{{else}} style="display: none;"{{/or}}>
            <div class="price-section--label">
                {{theme_settings.pdp-retail-price-label}}
            </div>

            <div class="price-section--wrapper">
                {{#if price.with_tax}}
                    <div class="price-section--withTax rrp-price--withTax{{#if price.without_tax}}{{#if theme_settings.MRRP-primary-price '==' 'with_tax'}} price-section--primary{{else}} price--hidden{{/if}}{{/if}}">
                        <span data-product-rrp-with-tax class="price price--rrp">
                            {{price.rrp_with_tax.formatted}}
                        </span>
                    </div>
                {{/if}}
                {{#if price.without_tax}}
                    <div class="price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}}{{#if theme_settings.MRRP-primary-price '==' 'without_tax'}} price-section--primary{{else}} price--hidden{{/if}}{{/if}}">
                        <span data-product-rrp-price-without-tax class="price price--rrp">
                            {{price.rrp_without_tax.formatted}}
                        </span>
                    </div>
                {{/if}}
            </div>
        </div>
        <div class="{{#unless theme_settings.show_price_below_name '===' 'right'}}buy-section-price{{/unless}} price-section"{{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}{{else}} style="display: none;"{{/or}}>
            <div class="price-section--label price--sale-label">
                {{theme_settings.pdp-non-sale-price-label}}
            </div>

            <div class="price-section--wrapper">
                {{#if price.with_tax}}
                    <div class="price-section--withTax non-sale-price--withTax{{#if price.without_tax}}{{#if theme_settings.primary-price '==' 'with_tax'}} price-section--primary{{else}} price--hidden{{/if}}{{/if}}">
                        <span data-product-non-sale-price-with-tax class="price price--non-sale">
                            {{price.non_sale_price_with_tax.formatted}}
                        </span>
                    </div>
                {{/if}}
                {{#if price.without_tax}}
                    <div class="price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}}{{#if theme_settings.primary-price '==' 'without_tax'}} price-section--primary{{else}} price--hidden{{/if}}{{/if}}">
                        <span data-product-non-sale-price-without-tax class="price price--non-sale">
                            {{price.non_sale_price_without_tax.formatted}}
                        </span>
                    </div>
                {{/if}}
            </div>
        </div>
        <div class="price-section">
            <div class="price-section--label" style="display: none;">
                <span class="price-label" >
                    {{theme_settings.pdp-price-label}}
                </span>
                <span class="price-now-label">
                    {{theme_settings.pdp-sale-price-label}}
                </span>
            </div>
            <div class="price-section--wrapper">
                {{#if price.with_tax}}
                    <div class="price-section--withTax{{#if price.without_tax}}{{#if theme_settings.primary-price '==' 'with_tax'}} price-section--primary{{/if}}{{else}} price-section--primary{{/if}}">
                        <span data-product-price-with-tax class="price price--withTax{{#if price.without_tax}}{{#if theme_settings.primary-price '==' 'with_tax'}} price-primary{{/if}}{{else}} price-primary{{/if}}">{{price.with_tax.formatted}}</span>
                        {{#if price.without_tax}}
                            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
                        {{else}}
                            {{#if theme_settings.show-tax-label}}
                                <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
                            {{/if}}
                        {{/if}}
                    </div>
                {{/if}}
                {{#if price.without_tax}}
                    <div class="price-section--withoutTax{{#if price.with_tax}}{{#if theme_settings.primary-price '==' 'without_tax'}} price-section--primary{{/if}}{{else}} price-section--primary{{/if}}">
                        <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}}{{#if theme_settings.primary-price '==' 'without_tax'}} price-primary{{/if}}{{else}} price-primary{{/if}}">{{price.without_tax.formatted}}</span>
                        {{#and price.with_tax price.without_tax}}
                            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
                        {{else}}
                            {{#if theme_settings.show-tax-label}}
                                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
                            {{/if}}
                        {{/and}}
                    </div>
                {{/if}}
            </div>
        </div>
    {{#if page_type '===' 'product'}}
         <div class="{{#unless theme_settings.show_price_below_name '===' 'right'}}buy-section-price{{/unless}} price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
         </div>
    {{/if}}
{{/and}}

