components/common/header:
{{{region name="above_header_banner--global"}}}
{{{region name="above_header_banner"}}}
{{#if banners.top}}
    {{#if settings.data_tag_enabled}}
        <div class="banners" data-banner-location="top">
            {{#each (limit banners.top_metadata 1)}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners" data-banner-location="top">
            <div class="banner">
                {{{limit banners.top 1}}}
            </div>
        </div>
    {{/if}}
{{/if}}
{{{region name="above_header--global"}}}
{{{region name="above_header"}}}
<header class="header {{#if customer}}header--customer {{#if theme_settings.logo_size '===' 'original'}} header--logo-original{{/if}}{{#if customer.store_credit.value "===" 0}}header--no-credit{{/if}}{{/if}} {{#if settings.gift_certificates_enabled}}header--gift-certificate-enabled{{else}}header--gift-certificate-disabled{{/if}} {{#and (if (length (split settings.store_logo.title "")) ">" 9) (unless settings.store_logo.image)}}header--logo-large{{/and}} {{#if (first (split theme_settings.logo_size "x")) ">" 250}}header--logo-large{{/if}} {{#if page_type '===' 'product'}}header--no-margin{{/if}} {{#unless theme_settings.show-quick-add-sku}}header--no-quick-add-sku{{/unless}} {{#or settings.store_logo.image theme_settings.svg_logo}} header--logo-image{{/or}} header--nav-{{ theme_settings.navigation_design }} header--width-{{theme_settings.header_width}}" role="banner">
  <div class="header__inner">
    <a href="#" class="mobileMenu-toggle {{#and (if (length (split settings.store_logo.title "")) ">" 9) (unless settings.store_logo.image)}}mobileMenu-toggle--text-logo{{/and}}" data-mobile-menu-toggle="menu">
        <span class="mobileMenu-toggleIcon">Toggle menu</span>
    </a>

    <div class="header-logo {{#if theme_settings.svg_logo}} header-logo--image {{#if (first (split theme_settings.logo_size "x")) ">" 250}}header-logo--large{{/if}}{{/if}}{{#if theme_settings.logo_size '===' 'original'}} header-logo--original{{/if}}{{#if settings.store_logo.image}} header-logo--image {{#if (first (split theme_settings.logo_size "x")) ">" 250}}header-logo--large{{/if}}{{/if}} {{#and (if (length (split settings.store_logo.title "")) ">" 9) (unless settings.store_logo.image)}}header-logo--large header-logo--text{{/and}} header-logo--{{theme_settings.logo-position}} {{#if theme_settings.enable_sticky_navigation}}sticky{{/if}}  {{#if (first (split theme_settings.logo_size "x")) "<=" 250}}{{#if settings.gift_certificates_enabled}}header-logo--gift-certificate-enabled{{/if}}{{/if}}">
        {{> components/common/store-logo}}
    </div>

    <div class="search-utils {{#unless theme_settings.show-quick-add-sku}}search-utils--no-quick-add-sku{{/unless}}">
        <div class="search {{#unless theme_settings.show-quick-add-sku}}search--no-quick-add-sku{{/unless}}" id="quickSearch" aria-hidden="true" tabindex="-1" data-prevent-quick-search-close>
            {{#and (if theme_settings.restrict_to_login '===' 2) customer}}
                {{> components/common/quick-search}}
            {{else}}
                {{#if theme_settings.restrict_to_login '!==' 2}}
                    {{> components/common/quick-search}}
                {{/if}}
            {{/and}}
        </div>

        {{#all theme_settings.show-quick-add-sku (if theme_settings.restrict_to_login '===' 2) customer}}
            {{> components/f/b2b/quick-add-form}}
        {{else}}
            {{#and theme_settings.show-quick-add-sku (if theme_settings.restrict_to_login '!==' 2)}}
                {{> components/f/b2b/quick-add-form}}
            {{/and}}
        {{/all}}
    </div>

    {{> components/f/header/top-utilities}}

  </div>
  <div class="header__bottom-nav {{#if customer}}header__bottom-nav--customer{{/if}}">
    <div class="header__bottom-nav-inner">
      <div class="navPages-container {{#if theme_settings.enable_sticky_navigation}}sticky{{/if}}" id="menu" data-menu>
          {{> components/common/navigation-menu}}
      </div>
      {{> components/f/header/nav-utilities}}
    </div>
  </div>

</header>
{{{region name="below_header--global"}}}
{{{region name="below_header"}}}
{{{region name="header_bottom"}}}

components/f/navigation/navigation-categ:
{{#and (unless navigation_design '===' 'simple--enclosed') (unless navigation_design '===' 'megamenu')}}
    {{#each categories}}
        {{#or (unless ../hide_category_over_amount) (if @index '<' ../hide_category_over_amount)}}
            {{#and ../../hide_navigation_featured_categories (if name '===' ../../featured_category_name)}}
            {{else}}
                <li class="navPages-item">
                    {{> components/f/navigation/navigation-list navigation_design=../../../navigation_design hide_view_all=../../../hide_view_all}}
                </li>
            {{/and}}
        {{/or}}
    {{/each}}
    {{#if enable_hide_category_over_amount}}
        <li class="navPages-item">
            <a class="navPages-action  has-subMenu is-root" href="{{url}}" data-collapsible="navPages-viewAll">
                {{lang hide_category_menu_name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
            </a>
            <div class="navPage-subMenu navPage-subMenu-horizontal navPage-subMenu--{{../../navigation_design}}" id="navPages-viewAll" aria-hidden="true" tabindex="-1">
                <ul class="navPage-subMenu-list">
                    {{#each categories}}
                        {{#if @index '>=' ../../hide_category_over_amount}}
                            {{#and ../../hide_navigation_featured_categories (if name '===' ../../featured_category_name)}}
                            {{else}}
                                <li class="navPage-subMenu-item-child">
                                    {{#if children}}
                                        <a class="navPage-subMenu-action navPages-action has-subMenu" href="{{url}}" data-collapsible="navPages-{{id}}">
                                            {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                                        </a>

                                        {{> components/f/navigation/navigation-dropdown
                                            navigation_design=../../../../navigation_design
                                            hide_view_all=../../../../hide_view_all
                                            hide_category_image_on_categories=../../../../hide_category_image_on_categories
                                        }}
                                    {{else}}
                                        <a class="navPage-subMenu-action navPages-action-depth-max navPages-action" href="{{url}}">{{name}}</a>
                                    {{/if}}
                                </li>
                            {{/and}}
                        {{/if}}
                    {{/each}}
                </ul>
            </div>
        </li>
    {{/if}}
{{/and}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'twitter'}}
                    <a class="icon icon--x" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-x" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
    <li class="navUser-item">
        <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">
            {{currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
        </a>
        <ul class="dropdown-menu dropdown-menu--arrow-right" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item currency-menu-item">
                {{{flag}}}
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
{{/if}}

components/f/navigation/navigation-list:
{{#if children}}
    {{#if name}}
        <a class="navPages-action{{#if navPageId '===' 'contextual'}} navPages-action-depth-max{{/if}} has-subMenu is-root" href="{{url}}" data-collapsible="navPages{{#if navPageId}}-{{navPageId}}{{/if}}-{{id}}">
            {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
        </a>
    {{/if}}
    {{> components/f/navigation/navigation-dropdown navigation_design=navigation_design navPageId=navPageId hide_view_all=hide_view_all}}
{{else}}
    <a class="navPages-action{{#unless navPageId '===' 'contextual'}}  navPages-action-depth-max{{/unless}}" href="{{url}}">{{name}}</a>
{{/if}}
